    background-color: #f5f5f5;
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	data.table = [];
	var gr = new GlideRecord('x_1299917_news_por_news');
	var count = 0;
	//gr.addEncodedQuery('active=true');
	gr.orderBy('number');
	gr.query();

	while(gr.next()) {
		count++;
		var obj={};
		obj.id = count;
		obj.number = gr.getValue('number');
		obj.headline = gr.getValue('headline');
		obj.shortDesc = gr.getValue('short_description');
		obj.publisher = gr.getDisplayValue('u_reference_1');
		data.table.push(obj);
	}

})();
